mapper.select:
	select * from wxorder 
	where 1=1

		and orderId = #{orderid,jdbcType=VARCHAR}


		and wxopenId = #{wxopenid,jdbcType=VARCHAR}



             and startTime >= #{startTime} and startTime <= #{startTime}



	limit 
	#{start},#{pageSize}

	select count(1) from wxorder 
	where 1=1

		and orderId = #{orderid,jdbcType=VARCHAR}


		and wxopenId = #{wxopenid,jdbcType=VARCHAR}



             and startTime >= #{startTime} and startTime <= #{startTime}
